const processForm=o=>{const t=new FormData(o);t.append("form-name","contact"),fetch("/",{method:"POST",body:t}).then(()=>{o.innerHTML='<div class="confirmation"><p>Thanks for getting in touch. We\'ll reply to you shortly!</p></div>'}).catch(t=>{o.innerHTML=`<div class="form--error">Error: ${t}</div>`})},contactForm=document.querySelector("#bravery-contact");contactForm&&contactForm.addEventListener("submit",o=>{o.preventDefault(),processForm(contactForm)});